
/*
 * GET home page.
 */

exports.index = function(req, res){
  res.render('index', { title: 'express-simple-flash example' });
};

exports.simple = function(req, res) {
  req.session.flash.error = 'Example error'
  req.session.flash.errors.simple_field = ['Example field error 1', 'Example field error 2']
  req.session.flash.simple_input = req.body.simple_input
  res.redirect('back')
}